class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int m=arr.size();
        int n=arr[0].size();
        if(n==1 && m==1){
            return arr[0][0];
        }
        int ur=0;
        int dr=m-1;
        vector<vector<int>> lmao;
        while(ur<=dr){
	        int lc=0;
	        int rc=n-1;
        	while(lc<=rc){
        		set<pair<int,int>> st;
        		vector<int> v;
        		if(!st.count(make_pair(ur,lc))){
        			st.insert(make_pair(ur,lc));
        			v.pb(arr[ur][lc]);
        		}
        		if(!st.count(make_pair(ur,rc))){
        			st.insert(make_pair(ur,rc));
        			v.pb(arr[ur][rc]);
        		}
        		if(!st.count(make_pair(dr,lc))){
        			st.insert(make_pair(dr,lc));
        			v.pb(arr[dr][lc]);
        		}
        		if(!st.count(make_pair(dr,rc))){
        			st.insert(make_pair(dr,rc));
        			v.pb(arr[dr][rc]);
        		}

        		lc++;
        		rc--;
        		lmao.pb(v);
        	}
        	ur++;
        	dr--;
        }
        int ans=0;
        int zero=0,one=0;
        vi ones;
        vi zeros;

        vector<pair<int,int>> ds;
        for(auto &it : lmao){
        	if(it.size()==4){
        		int o=0;
        		for(auto &x : it){
        			if(x)o++;
        		}
        		ans+=min(o,4-o);
        	}else if(it.size()==2){
        		int o=0;
        		for(auto &x : it){
        			if(x)o++;
        		}
				if(o==0)ones.pb(2);
				else if(o==1)ones.pb(1);
				else ones.pb(0);

				if(o==0)zeros.pb(0);
				else if(o==1)zeros.pb(1);
				else zeros.pb(2);
        	}else{
        		if(it[0]==1)ans++;
        	}
        }

        // return 0;
        // No flip

       int s=ones.size();
       if(s==0)return ans;
       // cout<<s<<endl;
        sort(all(ones));
        sort(all(zeros));
        reverse(all(zeros));
       for(int i=1;i<s;i++)ones[i]+=ones[i-1];
       for(int i=s-2;i>=0;i--)zeros[i]+=zeros[i+1];
       int f=zeros[0];
       for(int i=1;i<s;i+=2){
       		f=min(f,ones[i]+(i+1<s?zeros[i+1]:0));
       }
       return ans+f;
    }	

};
