#define ll long long
#define pb push_back
class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>>grid2 = grid;
        ll n = grid.size();
        ll m = grid[0].size();
        ll beg1 = 0;
        ll end1 = n-1;
        ll total = 0;

        ll ones = 0;
        bool poke = false;
        while(beg1<=end1){
            vector<int>row1 = grid2[beg1];
            vector<int>row2 = grid2[end1];
            ll beg2 = 0;
            ll end2 = m-1;
            while(beg2<=end2){
                ll c1=0;
                if(beg1==end1 && beg2==end2){
                   if(grid2[beg1][beg2]==1)total++;
                }
                else if(beg1==end1){
                    if(grid2[beg1][beg2]==1)c1++;
                    if(grid2[beg1][end2]==1)c1++;
                    total+=min(c1,2-c1);
                    ll a = min(c1,2-c1);


                    if(a==1)poke=true;
                    if(c1==2)ones+=2;
                }
                else if(beg2==end2){
                    if(grid2[beg1][beg2]==1)c1++;
                    if(grid2[end1][beg2]==1)c1++;
                    total+=min(c1,2-c1);
                    ll a = min(c1,2-c1);


                    if(a==1)poke=true;
                    if(c1==2)ones+=2;

                }
                else{
                    if(grid2[beg1][beg2]==1)c1++;
                    if(grid2[beg1][end2]==1)c1++;
                    if(grid2[end1][beg2]==1)c1++;
                    if(grid2[end1][end2]==1)c1++;
                    total+=min(c1,4-c1);
                }
                beg2++;
                end2--;
            }
            beg1++;
            end1--;
        }
        if(ones%4==2 && !poke)total+=2;
        return total;

    }
};
