#include <vector>
#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& grid)
    {
        int n = grid.size();
        int m = grid[0].size();

        // 4 - 0,1 2 3,4
        // 3 - 0,1 2,3

        int ans=0;

        for(int i=0; i<n/2; i++)
        {
            for(int j=0; j<m/2; j++)
            {
                int ones=0, zeroes=0;
                if(grid[i][j]) ones++;

                if(grid[n-i-1][j]) ones++;

                if(grid[i][m-j-1]) ones++;

                if(grid[n-i-1][m-j-1]) ones++;

                ans += min(ones, 4 - ones);
            }
        }

        int possible=0, count=0;
        if(n%2!=0)
        {
            for(int j=0; j<m/2; j++)
            {
                if(grid[n/2][j] != grid[n/2][m-j-1])
                {
                    ans++;
                    possible++;
                }
                else
                {
                    if(grid[n/2][j]==1)
                    {
                        count++;
                    }
                }
            }
        }

        if(m%2!=0) // odd no. of columns
        {
            for(int i=0; i<n/2; i++)
            {
                if(grid[i][m/2] != grid[n-i-1][m/2])
                {
                    ans++;
                    possible++;
                }
                else
                {
                    if(grid[i][m/2]==1)
                    {
                        count++;
                    }
                }
            }
            // cout<<"here\n";
        }
        // cout<<n<<' '<<m<<' '<<grid[n/2][m/2]<<'\n';

        if(n%2!=0 && m%2!=0 && grid[n/2][m/2])
        {
            ans++;
        }

        if(possible==0 && count%2!=0) ans+=2;

        return ans;
    }
};
