class Solution:
    def minFlips(self, grid):
        rows = len(grid)
        cols = len(grid[0])
        flips = 0

        if rows % 2 == 1 and cols % 2 == 1:
            if grid[rows // 2][cols // 2] == 1:
                flips += 1

        shifts = [0] * 3
        if rows % 2 == 1:
            left = 0
            right = cols - 1
            while left < right:
                sum_lr = grid[rows // 2][left] + grid[rows // 2][right]
                shifts[sum_lr] += 1
                left += 1
                right -= 1

        if cols % 2 == 1:
            top = 0
            bottom = rows - 1
            while top < bottom:
                sum_tb = grid[top][cols // 2] + grid[bottom][cols // 2]
                shifts[sum_tb] += 1
                top += 1
                bottom -= 1

        if shifts[2] % 2 == 1:
            if shifts[1] > 0:
                flips += shifts[1]
            else:
                flips += 2
        else:
            flips += shifts[1]

        for i in range(rows // 2):
            for j in range(cols // 2):
                a = grid[i][j]
                b = grid[rows - 1 - i][j]
                c = grid[i][cols - 1 - j]
                d = grid[rows - 1 - i][cols - 1 - j]

                sum_quad = a + b + c + d

                if sum_quad != 0 and sum_quad != 4:
                    flips += min(sum_quad, 4 - sum_quad)

        return flips
