class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int ret = 0;
        int m = grid.size(), n = grid[0].size();
        for(int i = 0; i < grid.size() / 2; i++){
            for(int j = 0; j < grid[i].size() / 2; j++){
                int sum_g = grid[i][j] + grid[i][n - j - 1] + grid[m - i - 1][j] + grid[m - i - 1][n - j - 1];
                if(sum_g % 2 == 1) ret ++;
                else if(sum_g % 4 == 2) ret += 2;
            }
        }

        int one = 0;
        int col_p = 0;

        if(m % 2 == 1){
            for(int j = 0; j < n / 2; j++){
                if(grid[m/2][j] != grid[m/2][n - j - 1]){
                    col_p ++;
                }
                else if(grid[m/2][j] == 1) one += 2;
            }
        }

        int row_p = 0;
        if(n % 2 == 1){

            for(int j = 0; j < m / 2; j++){
                if(grid[j][n/2] != grid[m - 1 - j][n/2]){
                    row_p ++;
                }
                else if(grid[j][n/2] == 1) one += 2;
            }
        }


        if(col_p == 0 && row_p == 0){
            ret += one % 4;
        }
        else{
            ret += col_p + row_p;
        }

        if(n % 2 == 1 && m %2 == 1){
            ret += grid[m/2][n/2];
        }



        return ret;
    }
};
