class Solution:
    def minFlips(self, g: List[List[int]]) -> int:
        n,m = len(g),len(g[0])
        res = 0
        cnt = 0
        for i in range(n>>1):
            for j in range(m>>1):
                cnt_1 = sum([g[i][j],g[i][m - 1 - j],g[n - 1 - i][j],g[n - 1 - i][m - 1 - j]])
                if cnt_1 == 0:
                    continue
                elif cnt_1 == 1:
                    res += 1
                elif cnt_1 == 2:
                    res += 2
                elif cnt_1 == 3:
                    res += 1
        e = 0
        if n&1:
            t = n>>1
            for j in range(m>>1):
                if g[t][j] == g[t][m - 1 - j]:
                    cnt += 2*g[t][j]
                    continue
                else: 
                    e |= 1
                    res += 1
        if m&1:
            t = m>>1
            for i in range(n>>1):
                if g[i][t] == g[n - 1 - i][t]:
                    cnt += 2*g[i][t]
                    continue
                else: 
                    e |= 1
                    res += 1
        if n&1 and m&1 and g[n>>1][m>>1]:
            res += 1
        return res if cnt%4 == 0 or e else res + 2
