class Solution {
public:
    int minFlips(vector<vector<int>>& mat) {
        int n = mat.size();
        int m = mat[0].size();
        int total = 0;
        int ans = 0;
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                vector<int> cnt(2);
                cnt[mat[i][j]]++;
                cnt[mat[n - i - 1][j]]++;
                cnt[mat[i][m - j - 1]]++;
                cnt[mat[n - i - 1][m - j - 1]]++;
                if(cnt[1] >= cnt[0]) total += 4;
                ans += min(cnt[0],cnt[1]);
            }
        }
        int have = 0;
        if(n & 1){
            for(int j = 0; j < m / 2; j++){
                if(mat[n / 2][j] == 1) total += 2;
                if(mat[n / 2][j] != mat[n / 2][m - j - 1]) {
                    ans++;
                    have++;
                }
            }
        }
        if(m & 1){
            for(int i = 0; i < n / 2; i++){
                if(mat[i][m / 2] == 1) total += 2;
                if(mat[i][m / 2] != mat[n - i - 1][m / 2]) {
                    ans++;
                    have++;
                }
            }
        }
        if((n & 1) && (m & 1)){
            ans += (mat[n / 2][m / 2]);
        }
        if(total % 4 == 2){
            if(have < 1){
                ans += 2;
            }
        }
        return ans;

    }
};
