class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size(), m = grid[0].size(), ans = 0;

        vector<int> arr2(3,0);

        for( int i=0; i<n/2; i++ )
        {
            for( int j=0; j<m/2; j++ )
            {
                int x = grid[i][j] + grid[n-i-1][j] + grid[i][m-j-1] + grid[n-i-1][m-j-1];
                ans += min(x,4-x);
            }
        }

        if( n%2==1 )
        {
            for( int i=0; i<m/2; i++ )
            {
                int x = grid[n/2][i] + grid[n/2][m-1-i];
                ans += x%2;
                arr2[x]++;
            }
        }
        if( m%2==1 )
        {
            for( int i=0; i<n/2; i++ )
            {
                int x = grid[i][m/2] + grid[n-i-1][m/2];
                ans += x%2;
                arr2[x]++;
            }
        }
        if( n%2==1 && m%2==1 )
        {
            if( arr2[2]%2==0 || (arr2[2]%2==1 && arr2[1]>0) )
            {
                if( grid[n/2][m/2]==1 )return ans+1;
                else return ans;
            }
            else{
                if( grid[n/2][m/2]==1 )return ans+1+2;
                else return ans+2;
            }
        }
        if( arr2[2]%2==0 || (arr2[2]%2==1 && arr2[1]>0) )return ans;

        return ans+2;
    }
};
