class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), sum = 0, ans = 0;
        vector<int> row(n, 0);
        for(int i = 0 ; i < n ; i++ ) {
            for(int j = 0 ; j < m ; j++ ) {
                sum += grid[i][j];
            }
        }
        vector<int> count(4, 0);
        for(int i = 0 ; i < n/2 ; i++ ) {
            for(int j = 0 ; j < m/2 ; j++ ) {
                int total = grid[i][j] + grid[i][m-j-1] + grid[n-i-1][j] + grid[n-i-1][m-j-1];
                count[total%4]++;
            }
        }
        ans = count[1] + count[3] + 2*count[2];
        count.assign(2, 0);
        if(m%2) {
            for(int i = 0 ; i < n/2 ; i++ ) {
                if(grid[i][m/2] != grid[n-i-1][m/2]) {
                    count[1]++;
                } else if (grid[i][m/2] == 1){
                    count[0] += 2;
                }
            }
        }
        if(n%2) {
            for(int i = 0 ; i < m/2 ; i++ ) {
                if(grid[n/2][i] != grid[n/2][m-i-1]) {
                    count[1]++;
                } else if (grid[n/2][i] == 1){
                    count[0] += 2;
                }
            } 
        }
        if(n%2 && m%2) count[0] += grid[n/2][m/2];
        if((count[0] + 2*count[1])%4 == 3) {
            if(count[1]) {
                ans += count[1]+1;
            } else {
                ans += 3;
            }
        } else if ((count[0] + 2*count[1])%4 == 2){
            if(count[1]) {
                ans += count[1];
            } else {
                ans += 2;
            }
        } else if ((count[0] + 2*count[1])%4 == 1){
            ans += count[1]+1;
        } else {
            ans += count[1];
        }
        return ans;
    }
};
