class Solution {
    public int minFlips(int[][] grid) {
        int res = 0;
        int n = grid.length;
        int m = grid[0].length;
        int a0=0,a2=0,a4=0;
        for(int i=0;i<(n+1)/2;i++){
            for(int j=0;j<(m+1)/2;j++){
                int x1 = i; int y1 = j;
                int x2 = i; int y2 = m-1-j;
                int x3 = n-1-i; int y3 = j;
                int x4 = n-1-i; int y4 = m-1-j;
                if(x1!=x3&&y1!=y2){
                    int su = grid[x1][y1] + grid[x2][y2] + grid[x3][y3] + grid[x4][y4];
                    res += Math.min(su , 4-su);
                    continue;
                }
                if(x1==x3&&y1==y2){
                    if(grid[x1][y1]==1) res++; continue;
                }
                int su = grid[x1][y1] + grid[x2][y2] + grid[x3][y3] + grid[x4][y4];
                if(su == 0)a0++;if(su == 2)a2++;if(su == 4)a4++;
            }
        }
        res += a2;
        if((a4 & 1) ==1){
            if(a2 == 0) res += 2;
        }
        return res;
    }
}
