class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int count = 0;
        int [] cVal = new int[]{0,1,2,1,0};
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                int col = 0;
                col += grid[i][j];
                col += grid[i][m-j-1];
                col += grid[n-i-1][j];
                col += grid[n-i-1][m-j-1];
                count += cVal[col];
            }
        }
        if(n%2==1&&m%2==1) {
            int x = n/2;
            int y = m/2;
            count+=grid[x][y];
            grid[x][y]=0;
            int ones = 0;
            int changes = 0;
            for(int i=0;i<m/2;i++) {
                if(grid[x][i]!=grid[x][m-i-1]) {
                    grid[x][i]=0;
                    grid[x][m-i-1]=0;
                    changes++;
                }
                ones+=grid[x][i]+grid[x][m-i-1];
            }
            for(int i=0;i<n/2;i++) {
                if(grid[i][y]!=grid[n-i-1][y]) {
                    grid[i][y]=0;
                    grid[n-i-1][y]=0;
                    changes++;
                }
                ones+=grid[i][y]+grid[n-i-1][y];
            }
            if(ones%4==2&&changes==0) {
                changes+=2;
            }
            count+=changes;
        } else if(n%2==1) {
            int x = n/2;
            int ones = 0;
            int changes = 0;
            for(int i=0;i<m/2;i++) {
                if(grid[x][i]!=grid[x][m-i-1]) {
                    grid[x][i]=0;
                    grid[x][m-i-1]=0;
                    changes++;
                }
                ones+=grid[x][i]+grid[x][m-i-1];
            }
            if(ones%4==2&&changes==0) {
                changes+=2;
            }
            count+=changes;
        } else if(m%2==1) {
            int y = m/2;
            int ones = 0;
            int changes = 0;
            for(int i=0;i<n/2;i++) {
                if(grid[i][y]!=grid[n-i-1][y]) {
                    grid[i][y]=0;
                    grid[n-i-1][y]=0;
                    changes++;
                }
                ones+=grid[i][y]+grid[n-i-1][y];
            }
            if(ones%4==2&&changes==0) {
                changes+=2;
            }
            count+=changes;
        }
        return count;
    }
}
