class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        if(n==1 && m==1){
            return grid[0][0];
        }
        int ans = 0;
        int left,right;
            left = m/2;
            right = n/2;

        for(int j = 0;j<(left);j++){
        for(int i = 0;i<(right);i++){
            int cnt = 0;
            cnt +=grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j];
            ans +=min(cnt, (4-cnt));
        }
        }
        cout<<ans<<endl;
        int two = 0,one = 0;
        if(n%2 == 1){
            int y = (n+1)/2 -1;
            for(int j = 0;j<(m/2);j++){
                int cnt = grid[y][j] + grid[y][m-1-j];
                if(cnt == 2){
                    two++;
                }
                else if(cnt == 1){
                    one++;
                }
            }
        }
        if(m%2 == 1){
            int x = (m+1)/2 -1 ;
            for(int i = 0;i<(n/2);i++){
                int cnt = grid[i][x] + grid[n-1-i][x];
                if(cnt == 2){
                    two++;
                }
                else if(cnt == 1){
                    one++;
                }
            }
        }
        cout<<ans<<endl;
        if(n%2 == 1 && m%2==1){
            ans+=grid[(n)/2][(m)/2];
           //  cout<<(n+1)/2<<" "<<(n+1)/2<<endl;
        }
        cout<<two<<" "<<one<<endl;
        cout<<ans<<endl;

        if(two%2!=0 && one == 0){
           ans+=2;
        }
        ans+=one;
        return ans;
    }
};
