class Solution {
public:
    int minFlips(vector<vector<int>>& nums) {
        int n=nums.size(),m=nums[0].size();
        int ans=0;
        for(int i=0 ; i<n/2 ; i++){
            for(int j=0 ; j<m/2 ; j++){
                int ones=0;
                ones=nums[i][j]+nums[n-i-1][j]+nums[i][m-1-j]+nums[n-i-1][m-j-1];
                ans+=(4-max(ones,4-ones));
            }
        }
        int rowCnt=0,rowOnes=0;
        int colCnt=0,colOnes=0;
        if(n%2){
            for(int j=0 ; j<m/2 ; j++){
                if(nums[n/2][j]!=nums[n/2][m-1-j]) rowCnt++;
                else rowOnes+=(nums[n/2][j]*2);
            }
        }
        if(m%2){
            for(int i=0 ; i<n/2 ; i++){
                if(nums[i][m/2]!=nums[n-i-1][m/2]) colCnt++;
                else colOnes+=(nums[i][m/2]*2);
            }
        }
        if(n%2 && m%2 && nums[n/2][m/2]) ans++;
        int ones=colOnes+rowOnes;
        int not_equal=rowCnt+colCnt;
        ans+=not_equal;
        if(ones%4==2 && not_equal==0) ans+=2;
        return ans;
    }
};
