class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                r = [grid[i][j], grid[i][~j], grid[~i][j], grid[~i][~j]]
                ans += min(4 - r.count(1), 4 - r.count(0))

        e, cnt1 = 0, 0
        if m & 1 and not n & 1:
            for i in range(n // 2):
                if grid[m // 2][i] != grid[m // 2][~i]:
                    e += 1
                elif grid[m // 2][i] == 1:
                    cnt1 += 2
        elif n & 1 and not m & 1:
            for i in range(m // 2):
                if grid[i][n // 2] != grid[~i][n // 2]:
                    e += 1
                elif grid[i][n // 2] == 1:
                    cnt1 += 2
        elif n & 1 and m & 1:
            for i in range(n // 2):
                if grid[m // 2][i] != grid[m // 2][~i]:
                    e += 1
                elif grid[m // 2][i] == 1:
                    cnt1 += 2
            for i in range(m // 2):
                if grid[i][n // 2] != grid[~i][n // 2]:
                    e += 1
                elif grid[i][n // 2] == 1:
                    cnt1 += 2
            if grid[m // 2][n // 2] == 1:
                ans += 1
        ans += e
        if cnt1 % 4 + e * 2 >= 4 or cnt1 % 4 == 0:
            return ans
        return ans + 4 - (cnt1 % 4 + e * 2)
