class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for (int i=0;i<n/2;i++)
        for (int j=0;j<m/2;j++)
        {
            int now=grid[i][j]+grid[n-1-i][j]+grid[i][m-j-1]+grid[n-1-i][m-1-j];
            ans+=min(now,4-now);
        }
        int t1=0,t2=0;//t1需要变的队数，t2 1的队数
        int h=n/2;
        cout<<ans<<endl;
        if (n&1)
        for (int j=0;j<m/2;j++) 
            t1+=grid[h][j]==grid[h][m-1-j]?0:1,t2+=grid[h][j]+grid[h][m-1-j]==2?1:0;
        int l=m/2;
        if (m&1)
        for (int i=0;i<n/2;i++) 
            t1+=grid[i][l]==grid[n-1-i][l]?0:1,t2+=grid[i][l]+grid[n-1-i][l]==2?1:0;
        ans+=t1;
        if (((n&1)||(m&1))&&(t2&1))
        {

            if (t1==0) ans+=2;
        }
        if ((n&1)&&(m&1)) ans+=grid[n/2][m/2];
        //cout<<t1<<" "<<t2<<endl;
        return ans;
    }
};
