class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int cnt[2]={0};
                cnt[grid[i][j]]++;
                cnt[grid[i][m-1-j]]++;
                cnt[grid[n-1-i][j]]++;
                cnt[grid[n-1-i][m-1-j]]++;
                ans+=min(cnt[0],cnt[1]);
            }
        }


         if(n%2  && m%2 && grid[n/2][m/2])ans++;
         int cnt1=0,cnt11=0;
         if(n%2)
         {
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]==grid[n/2][m-1-i])
                {
                    cnt11+= (grid[n/2][i]);
                }
                else cnt1++;
            }
         }
         if(m%2)
         {
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]==grid[n-1-i][m/2])
                {
                    cnt11+= (grid[i][m/2]);
                }
                else cnt1++;
            }
         }

        cnt11%=2;
        if(cnt1)
        {
            ans+=cnt1;
        }
        else ans+=2*cnt11;

             return ans;
    }
};
