class Solution {
    public int minFlips(int[][] arr){
        int r = arr.length;
        int c = arr[0].length;
        boolean[][] changed = new boolean[r][c];

        for(int i=0,j=r-1;i<j;i++,j--){
            for(int k=0,l=c-1;k<l;k++,l--){
                int one = 0;
                int zero = 0;
                if(arr[i][k] == 1) one++;
                else zero++;
                if(arr[i][l] == 1) one++;
                else zero++;
                if(arr[j][k] == 1) one++;
                else zero++;
                if(arr[j][l] == 1) one++;
                else zero++;
                //True one>zero
                boolean chnageTo = true;
                if(one<zero) chnageTo=false;

                if(chnageTo) {
                    if (arr[i][k] == 0) {
                        arr[i][k] = 1;
                        changed[i][k] = true;
                    }
                    if (arr[i][l] == 0) {
                        arr[i][l] = 1;
                        changed[i][l] = true;
                    }
                    if (arr[j][k] == 0) {
                        arr[j][k] = 1;
                        changed[j][k] = true;
                    }
                    if (arr[j][l] == 0) {
                        arr[j][l] = 1;
                        changed[j][l] = true;
                    }
                }
                else{
                    if (arr[i][k] == 1) {
                        arr[i][k] = 0;
                        changed[i][k] = true;
                    }
                    if (arr[i][l] == 1) {
                        arr[i][l] = 0;
                        changed[i][l] = true;
                    }
                    if (arr[j][k] == 1) {
                        arr[j][k] = 0;
                        changed[j][k] = true;
                    }
                    if (arr[j][l] == 1) {
                        arr[j][l] = 0;
                        changed[j][l] = true;
                    }
                }

            }
        }
        // for(int[] i : arr){
        //     System.out.println(Arrays.toString(i));
        // }

        if(r%2!=0){
            int i = r/2;
            for(int k=0,l=c-1;k<l;k++,l--){
                if(arr[i][k]!=arr[i][l]){
                    if(arr[i][k]==0){
                        arr[i][k] = 1;
                        changed[i][k] = true;
                    }
                    else{
                        arr[i][l] = 1;
                        changed[i][l] = true;
                    }
                }
            }
        }
        if(c%2!=0){
            int i = c/2;
            for(int k=0,l=r-1;k<l;k++,l--){
                if(arr[k][i]!=arr[l][i]){
                    if(arr[k][i]==0){
                        arr[k][i] = 1;
                        changed[k][i] = true;
                    }
                    else{
                        arr[l][i] = 1;
                        changed[l][i] = true;
                    }
                }
            }
        }

        // for(int[] i : arr){
        //     System.out.println(Arrays.toString(i));
        // }


        int oneCount = 0;
        int flipCount = 0;
        for(int i=0;i<r;i++){
            for(int j=0;j<c;j++){
                oneCount += arr[i][j];
                if(changed[i][j]) flipCount++;
            }
        }
        // System.out.println(flipCount);


        int rem = oneCount%4;
        if(rem == 0){
            return flipCount;
        }
        if(rem==1){
            if(r%2!=0 && c%2!=0){
                if(arr[r/2][c/2]==1){
                    if(changed[r/2][c/2]) return flipCount-1;
                    else return flipCount+1;
                }

            }
        }
        if(rem==2){
            if(r%2!=0){
                int i = r/2;
                for(int j=0,k=c-1;j<k;j++,k--){
                    if(arr[i][j]==1){
                        if(changed[i][j] || changed[i][k]){
                            return flipCount;
                        }
                    }
                }
                if(c%2==0) return flipCount+2;
            }
            if(c%2!=0){
                int i = c/2;
                for(int j=0,k=r-1;j<k;j++,k--){
                    if(arr[j][i]==1){
                        if(changed[j][i] || changed[k][i]){
                            return flipCount;
                        }
                    }
                }
                return flipCount+2;
            }
        }

        if(rem==3){
            if(r%2!=0 && c%2!=0){
                if(arr[r/2][c/2]==0){
                    if(changed[r/2][c/2]) return flipCount-1;
                    else return flipCount+1;
                }
                else{
                    arr[r/2][c/2] = 0;
                    if(changed[r/2][c/2]) flipCount--;
                    else flipCount++;
                }
            }
            // System.out.println(flipCount);
            if(r%2!=0){
                int i = r/2;
                for(int j=0,k=c-1;j<k;j++,k--){
                    if(arr[i][j]==1){
                        if(changed[i][j] || changed[i][k]){
                            return flipCount;
                        }
                    }
                    if(c%2==0)return flipCount+2;
                }

            }
            // System.out.println("Reached");
            if(c%2!=0){
                int i = c/2;
                for(int j=0,k=r-1;j<k;j++,k--){
                    if(arr[j][i]==1){
                        if(changed[j][i] || changed[k][i]){
                            return flipCount;
                        }
                    }
                }
                return flipCount+2;
            }
        }

        return flipCount;



    }

}
