class Solution {
    public int minFlips(int[][] grid) {
        int r1=0;
        int r2=grid.length-1;
        int cnt=0;
        while(r1<r2){
            int j1=0;
            int j2=grid[0].length-1;
            while(j1<j2){
                int one1=0;
                int zero1=0;
                if(grid[r1][j1]==0){
                    zero1++;
                }
                else{
                    one1++;
                }
                if(grid[r1][j2]==0){
                    zero1++;
                }
                else{
                    one1++;
                }
                if(grid[r2][j1]==0){
                    zero1++;
                }
                else{
                    one1++;
                }
                if(grid[r2][j2]==0){
                    zero1++;
                }
                else{
                    one1++;
                }
                if(one1>zero1){
                    cnt+=zero1;
                }
                else{
                    cnt+=one1;
                }
                j1++;
                j2--;
            }
            r1++;
            r2--;
        }
        // System.out.println(cnt);
        int cnt1=0;
        int onepair=0;
        if(grid.length%2!=0){
            int x=grid.length/2;
            int i=0;
            int j=grid[0].length-1;
            while(i<j){
                if(grid[x][i]!=grid[x][j]){
                    cnt1++;
                }
                else{
                    if(grid[x][i]==1){
                        onepair++;
                    }
                }
                i++;
                j--;
            }
        }
        if(grid[0].length%2!=0){
            int x=grid[0].length/2;
            int i=0;
            int j=grid.length-1;
            while(i<j){
                if(grid[i][x]!=grid[j][x]){
                    cnt1++;
                }
                else{
                    if(grid[i][x]==1){
                        onepair++;
                    }
                }
                i++;
                j--;
            }
        }
        // System.out.println(cnt1);
        if(onepair%2==0){
            cnt+=cnt1;
        }
        else{
            if(cnt1>0){
                cnt+=cnt1;
            }
            else{
                cnt+=2;
            }
        }
        if(grid.length%2!=0 && grid[0].length%2!=0 && grid[grid.length/2][grid[0].length/2]==1){
            cnt++;
        }
        return cnt;
    }
}
