class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
		int ret = 0;
		vector<int> a(3);
		for (int i = 0, ii = n-1; i <= ii; i++, ii--) {
			for (int j = 0, jj = m-1; j <= jj; j++, jj--) {
				if (i != ii && j != jj) {
					int c = grid[i][j] + grid[ii][jj] + grid[i][jj] + grid[ii][j];
					ret += min(c, 4-c);
				}
				if (i != ii && j == jj) {
					int c = grid[i][j] + grid[ii][j];
					a[c]++;
				}
				if (i == ii && j != jj) {
					int c = grid[i][j] + grid[i][jj];
					a[c]++;
				}
				if (i == ii && j == jj) {
					ret += grid[i][j];
				}
			}
		}
		ret += a[1];
		if (a[2] % 2 == 1) {
			if (a[1] == 0)
				ret += 2;
		}
		return ret;
    }
};
