class Solution {
public:
    int minFlips(vector<vector<int>>&a ) {
        int n=a.size();
        int m=a[0].size();
        int ans=0,cnt=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(a[i][j]==1) cnt++;
            }
        }
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt1=0;
                cnt1+=a[i][j];
                cnt1+=a[n-1-i][j];
                cnt1+=a[i][m-1-j];
                cnt1+=a[n-1-i][m-1-j];
                ans+=min(4-cnt1,cnt1);
            }
        }
        // cout<<ans<<endl;
        if(n%2 && m%2) ans+=a[n/2][m/2];
        int pp=0,oo=0;
        if(n%2){
            int l=0,r=m-1;
             int p=0;
            while(l<r){
                if(a[n/2][l]==a[n/2][r] && a[n/2][l]==1) p++;
                l++;r--;
            }
            l=0,r=m-1;
            int o=0;
            while(l<r){
                if(a[n/2][l]!=a[n/2][r]) ans++,o++;
                l++;r--;
            }
            pp=p;
            oo=o;
            // if(o==0 && p%2) ans+=2;
            // cout<<ans<<endl;
        }
        int ooo=0,ppp=0;
         if(m%2){
            int l=0,r=n-1;
            int p=0;
            while(l<r){
                if(a[l][m/2]==a[r][m/2] && a[l][m/2]==1) p++;
                l++;r--;
            }
            l=0,r=n-1;
            int o=0;
            while(l<r){
                if(a[l][m/2]!=a[r][m/2]) o++,ans++;
                l++;r--;
            }
            ppp=p;ooo=o;
            // if(o==0 && p%2) ans+=2;
        }
        if((pp%2 != ppp%2) && oo==0 && ooo==0) ans+=2;
        return min(ans,cnt);
    }
};
