class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size(),m=g[0].size();
        int ans=0,cur=0;
        int n1=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int r2=i,c2=m-1-j,r3=n-1-i,r4=n-1-i,c3=j,c4=m-1-j;
                int oc=0,zc=0;
                if(g[i][j]==1)
                oc++;
                else zc++;
                if(g[r2][c2]==1)
                oc++;
                else zc++;
                if(g[r3][c3]==1)
                oc++;
                else zc++;
                if(g[r4][c4]==1)
                oc++;
                else zc++;


                    if(i==r3 && j==c2){
                        if(g[i][j]) ans++;
                    }else if(i==r3){
                        ans++;
                    }else if(j==c2) ans++;
                    else{
                        ans+=min(oc,zc);
                    }


            }
        }
        int cnt=0,ex=0;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(g[n/2][i]!=g[n/2][m-1-i])
                {
                    ans++;
                    ex++;
                }
                else {
                    if(g[n/2][i]){
                        cnt++;
                    }
            }
            }
        }

        if(m%2){
            for(int i=0;i<n/2;i++){
                if(g[i][m/2]!=g[n-1-i][m/2])
                {
                    ans++;
                    ex++;
                }
                else {
                    if(g[i][m/2]){
                        cnt++;
                    }
            }
            }
        }
        if(n%2 && m%2){
            if(g[n/2][m/2])
            ans++;
        }

        if(cnt%2){
            if(ex==0){
                ans+=2;
            }
        }
        return ans;
    }
};
