class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int height = grid.size(), width = grid[0].size();
        int flip = 0, two = 0, sum = 0;
        for (int t = 0, b = height - 1; t <= b; t++, b--) {
            for (int l = 0, r = width - 1; l <= r; l++, r--) {
                if (l == r && t == b)
                    flip += grid[t][l];
                else if (l != r && t != b) {
                    int one = grid[t][l] + grid[b][l] + grid[t][r] + grid[b][r];
                    flip += min(one, 4 - one);
                } else if (l == r) {
                    if (grid[t][l] != grid[b][l]){
                        flip++;
                        two++;
                    } else {
                        sum += grid[t][l] + grid[b][l];
                    }
                } else if (t == b) {
                    if (grid[t][l] != grid[t][r]) {
                        flip++;
                        two++;
                    } else {
                        sum += grid[t][l] + grid[t][r];
                    }
                }
                // printf("l=%d, r=%d, t=%d, b=%d, sum=%d, flip=%d\n", l, r, t, b, sum, flip);
            }
        }

        if (two == 0 && sum % 4 != 0)
            flip += 2;
        return flip;
    }
};
