impl Solution {
    pub fn min_flips(g: Vec<Vec<i32>>) -> i32 {
        let n = g.len();
        let m = g[0].len();
        let mut a = 0;

        if n % 2 == 1 && m % 2 == 1 {
            if g[n / 2][m / 2] == 1 {
                a += 1;
            }
        }

        let mut s = vec![0; 3];
        if n % 2 == 1 {
            let mut x = 0;
            let mut y = m - 1;
            while x < y {
                let sm = g[n / 2][x] + g[n / 2][y];
                s[sm as usize] += 1;
                x += 1;
                y -= 1;
            }
        }

        if m % 2 == 1 {
            let mut x = 0;
            let mut y = n - 1;
            while x < y {
                let sm = g[x][m / 2] + g[y][m / 2];
                s[sm as usize] += 1;
                x += 1;
                y -= 1;
            }
        }

        if s[2] % 2 == 1 {
            if s[1] > 0 {
                a += s[1];
            } else {
                a += 2;
            }
        } else {
            a += s[1];
        }

        for i in 0..n / 2 {
            for j in 0..m / 2 {
                let v = g[i][j];
                let w = g[n - 1 - i][j];
                let x = g[i][m - 1 - j];
                let y = g[n - 1 - i][m - 1 - j];

                let sm = v + w + x + y;

                if sm != 0 && sm != 4 {
                    a += sm.min(4 - sm);
                }
            }
        }

        a
    }
}
