class Solution {
public:
     int cntflip(vector<int>&arr){
        int left=0;
        int right=arr.size()-1;
        int cnt=0;
        while(left<right){
            if(arr[left]!=arr[right]){
                cnt++;
            }
            left++;
            right--;
        }
        return cnt;
    }
    int minFlips(vector<vector<int>>& matrix) {


        int rows = matrix.size(), cols = matrix[0].size();
        int flips = 0;

        if (rows % 2 == 1 && cols % 2 == 1) {
            if (matrix[rows / 2][cols / 2] == 1) {
                flips += 1;
            }
        }

        vector<int> pairs(3, 0);
        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = matrix[rows / 2][left] + matrix[rows / 2][right];
                pairs[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = matrix[top][cols / 2] + matrix[bottom][cols / 2];
                pairs[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        if (pairs[2] % 2 == 1) {
            if (pairs[1] > 0) {
                flips += pairs[1];
            } else {
                flips += 2;
            }
        } else {
            flips += pairs[1];
        }

        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int tl = matrix[i][j];
                int bl = matrix[rows - 1 - i][j];
                int tr = matrix[i][cols - 1 - j];
                int br = matrix[rows - 1 - i][cols - 1 - j];

                int sum = tl + bl + tr + br;

                if (sum != 0 && sum != 4) {
                    flips += min(sum, 4 - sum);
                }
            }
        }

        return flips;
    }
};
