class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int a=grid[i][j];
                int b=grid[i][n-j-1];
                int c=grid[m-i-1][j];
                int d=grid[m-i-1][n-j-1];
                vector<int> v={a,b,c,d};
                int one=0,zero=0;
                for(auto x:v){
                    one+=(x==1);
                    zero+=(x==0);
                }
                if(zero>=one){
                    ans+=one;
                }
                else{
                    ans+=zero;
                }
            }
        }
        int c=0,f=1;
        if(m%2){

            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]!=grid[m/2][n-i-1]){
                    ans++;
                    f=0;
                }
                else{
                    if(grid[m/2][i]==1){
                        c++;
                    }
                }
            }
        }

        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]){
                    ans++;
                    f=0;
                }
                else{
                    if(grid[i][n/2]==1){
                        c++;
                    }
                }
            }
        }
        if(m%2 && n%2 && grid[m/2][n/2]){
            ans++;
        }
        if(c%2 && f){
            ans+=2;
        }
        return ans;
    }
};
