class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;

            int i1=0,i2=m-1;
            while(i1<i2) {
                int j1=0,j2=n-1;
                while(j1<j2) {
                    int zero_count=0;
                    if(grid[i1][j1]==0) {
                        zero_count++;
                    }
                    if(grid[i1][j2]==0) {
                        zero_count++;
                    }
                    if(grid[i2][j1]==0) {
                        zero_count++;
                    }
                    if(grid[i2][j2]==0) {
                        zero_count++;
                    }
                    if(zero_count==1 || zero_count==3) {
                        ans+=1;
                    }
                    if(zero_count==2) {
                        ans+=2;
                    }
                    j1++,j2--;
                }
                i1++,i2--;
            }

        // cout<<ans<<" "<<endl;
        int one_one_count=0;
        int one_zero_count=0;
        int mid_row = (m)/2;
        int mid_col = (n)/2;
        if(m&1) {
            for(int i=0,j=n-1;i<j;i++,j--) {
                if(grid[mid_row][i]!=grid[mid_row][j]) {
                    ans++;
                    one_zero_count++;
                }else if(grid[mid_row][i]==1) {
                    one_one_count+=2;
                }
            }
        }

        // cout<<ans<<" "<<endl;

        if(n&1) {
            for(int i=0,j=m-1;i<j;i++,j--) {
                if(grid[i][mid_col]!=grid[j][mid_col]) {
                    ans++;
                    one_zero_count++;
                }else if(grid[i][mid_col]==1) {
                    one_one_count+=2;
                }
            }
        }

        // cout<<ans<<" "<<endl;

        if(m&1 && n&1 && grid[mid_row][mid_col]==1) {
            ans++;
        }
        // cout<<ans<<" "<<endl;
        if(one_one_count%4!=0) {
            if(one_zero_count==0)
            ans+=2;
        }

        // cout<<ans<<" "<<endl;

        return ans;
    }
};
