class Solution {
    public int minFlips(int[][] grid) {
        int ans = 0;
        int n = grid.length;
        int m = grid[0].length;
        int[] arr=new int[3];
        if (n >= 2 && m >= 2) {
            for (int i = 0; i < n >> 1; i++) {
                for (int j = 0; j < m >> 1; j++) {
                    int one = 0;
                    one += grid[i][j];
                    one += grid[i][m - 1 - j];
                    one += grid[n - 1 - i][j];
                    one += grid[n - 1 - i][m - 1 - j];
                    if (one != 0 && one != 4) {
                        ans+=Math.min(4-one,one);
                    }
                }
            }
        }
        if ((n & 1) == 1) {
            for (int j = 0; j < m >> 1; j++) {
                int one = grid[n >> 1][j]+grid[(n >> 1)][m - 1 - j];
                if (one != 0 ) arr[one]++;
            }
        }
        if ((m & 1) == 1) {
            for (int i = 0; i < n >> 1; i++) {
                int one =  grid[i][m >> 1]+grid[n - 1 - i][m >> 1];
                if (one != 0 ) arr[one]++;
            }
        }
        // System.out.println(Arrays.toString(arr) +"  "+ans);
        if ((m & 1) == 1 && (n & 1) == 1) {
            ans += grid[n >> 1][m >> 1];
        }
        if((arr[2]&1)==1){
            if(arr[1]!=0){
                arr[1]--;
                ans+=1;
            }else {
                ans+=2; 
            }
        }
        ans+=arr[1];
        return ans;
    }
}
