class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0;
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            {
                int cnt=grid[i][j]+grid[i][n-1-j]+grid[m-1-i][j]+grid[m-1-i][n-j-1];
                if(cnt==4) cnt=0;
                if(cnt==3) cnt=1;
                ans+=cnt;
            }
        }
        int val1=0,v=0;
        if(n%2==1)
        {
            for(int i=0;i<m/2;i++)
            {
                if(grid[i][n/2]!=grid[m-i-1][n/2])
                {ans++;v++;}
                else if(grid[i][n/2]==1) val1++;
            }
        }
        // cout<<ans<<val1<<endl;
        if(m%2==1)
        {
            for(int i=0;i<n/2;i++)
            {
                if(grid[m/2][i]!=grid[m/2][n-i-1])
                {ans++;v++;}
                else if(grid[m/2][i]==1) val1++;

            }
        }
        if(m%2==1&&n%2==1&&grid[m/2][n/2]==1) ans++; 
        if(val1%2==1&&v==0) ans+=2;
        // ans+=(val1%2)*2;
        return ans;
    }
};
