class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rs=0,cs=0,re=grid.size()-1,ce=grid[0].size()-1;
        int ans=0,extra=0; bool buffer=0;
        while(rs<re){
            cs=0; ce=grid[0].size()-1;
            while(cs<ce){
                int x=0,y=0;
                if(grid[rs][cs]==1) x++;
                else y++;
                if(grid[re][cs]==1) x++;
                else y++;
                if(grid[rs][ce]==1) x++;
                else y++;
                if(grid[re][ce]==1) x++;
                else y++;
                // if(x==y){
                //     ans+=2; buffer=1;
                // }else 
                ans+=min(x,y);
                cs++; ce--;
            }
            if(cs==ce){
                if(grid[rs][cs]==grid[re][cs]) extra+= grid[rs][cs]+grid[re][cs];
                else{
                    ans++; buffer=1;
                }
            }
            rs++; re--;
        }
        if(rs==re){
            cs=0; ce=grid[0].size()-1;
            while(cs<ce){
                if(grid[rs][cs]!=grid[re][ce]){
                    ans++; buffer=1;
                }else extra+= grid[rs][cs]+grid[re][ce];
                cs++; ce--;
            }
            if(cs==ce){
                extra+= grid[rs][cs];
            }
        }
        if(extra&1){
            ans++; extra--;
        }
        if(extra%4!=0){
            if(!buffer){
                ans+=2;
                // cout<<"here"<<endl;
            }
        }
        return ans;
    }
};
