func minFlips(grid [][]int) int {
	n := len(grid)
	m := len(grid[0])
	ans := 0

	if n%2 == 1 && m%2 == 1 {
		if grid[n/2][m/2] == 1 {
			ans += 1
		}
	}

	shift := calculateShifts(grid, n, m)

	if shift[2]%2 == 1 {
		if shift[1] > 0 {
			ans += shift[1]
		} else {
			ans += 2
		}
	} else {
		ans += shift[1]
	}

	ans += calculateCornerFlips(grid, n, m)

	return ans
}

func calculateShifts(grid [][]int, n, m int) [3]int {
	shift := [3]int{0, 0, 0}

	if n%2 == 1 {
		shift = updateShiftForRow(grid, n, m, shift)
	}

	if m%2 == 1 {
		shift = updateShiftForColumn(grid, n, m, shift)
	}

	return shift
}

func updateShiftForRow(grid [][]int, n, m int, shift [3]int) [3]int {
	a, b := 0, m-1
	for a < b {
		sm := grid[n/2][a] + grid[n/2][b]
		shift[sm] += 1
		a += 1
		b -= 1
	}
	return shift
}

func updateShiftForColumn(grid [][]int, n, m int, shift [3]int) [3]int {
	a, b := 0, n-1
	for a < b {
		sm := grid[a][m/2] + grid[b][m/2]
		shift[sm] += 1
		a += 1
		b -= 1
	}
	return shift
}

func calculateCornerFlips(grid [][]int, n, m int) int {
	ans := 0

	for i := 0; i < n/2; i++ {
		for j := 0; j < m/2; j++ {
			a := grid[i][j]
			b := grid[n-1-i][j]
			c := grid[i][m-1-j]
			d := grid[n-1-i][m-1-j]

			sm := a + b + c + d

			if sm != 0 && sm != 4 {
				ans += min(sm, 4-sm)
			}
		}
	}

	return ans
}
