class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> turn(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int s = grid[n / 2][a] + grid[n / 2][b];
                turn[s] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (m % 2 == 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int s = grid[a][m / 2] + grid[b][m / 2];
                turn[s] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (turn[2] % 2 == 1) {
            if (turn[1] > 0) {
                ans += turn[1];
            } else {
                ans += 2;
            }
        } else {
            ans += turn[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];

                int s = a + b + c + d;

                if (s != 0 && s != 4) {
                    ans += min(s, 4 - s);
                }
            }
        }

        return ans;
    }
};
