class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        if(n==1&&m==1) {
            if(grid[0][0]==1) return 1;
            return 0;
        }
        if(n==1&&m==2)
        {
            return grid[0][0]+grid[0][1];
        }
        if(n==2&&m==1) {
            return grid[0][0]+grid[1][0];
        }
        int aRow = 0; int bRow = n-1; int moves = 0; int cnt1 =0; int cnt0 =0; int cnt2 = 0;
        while(aRow<=bRow)
        {
            int lCol = 0; int rCol = m-1;
            while(lCol<=rCol) {
            int l1 = grid[aRow][lCol];
            int l2 = grid[aRow][rCol];
            int l3 = grid[bRow][lCol];
            int l4 = grid[bRow][rCol];
            if(aRow!=bRow&&lCol!=rCol)
            {
                int s = l1+l2+l3+l4;
                if(s==2) moves = moves+2;
                else if(s==3||s==1) moves++;
            }
            else if(aRow==bRow&&lCol!=rCol){
                 if(l1==l2) {
                     if(l1==1) cnt1++;
                     else cnt0++;
                 }
                else {
                    cnt2++;
                }
            }
            else if(aRow!=bRow&&lCol==rCol)
              {
                    if(l1==l3)
                    {
                        if(l1==1) cnt1++;
                        else cnt0++;
                    }
                else  cnt2++;
               }
          else {
              if(l1==1) moves++;
          }
                lCol++; rCol--;
            }
           aRow++; bRow--; 
     }
        // System.out.println(cnt1+" "+cnt2+" "+cnt0);
       moves = moves+cnt2;
      if(cnt1%2!=0&&cnt2==0) moves = moves+2;
      return moves;
    }
}
