class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        const int rows = grid.size(), cols = grid[0].size();
        int flips = 0;

        flips += (rows & 1) && (cols & 1) && grid[rows / 2][cols / 2];

        vector<int> shift(3, 0);

        if (rows & 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                shift[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        if (cols & 1) {
            int top = 0, bot = rows - 1;
            while (top < bot) {
                int sum = grid[top][cols / 2] + grid[bot][cols / 2];
                shift[sum] += 1;
                top += 1;
                bot -= 1;
            }
        }

        if (shift[2] & 1) {
            if (shift[1] > 0) {
                flips += shift[1];
            } 
            else {
                flips += 2;
            }
        } 
        else {
            flips += shift[1];
        }

        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int a = grid[i][j];
                int b = grid[rows - 1 - i][j];
                int c = grid[i][cols - 1 - j];
                int d = grid[rows - 1 - i][cols - 1 - j];

                int sum = a + b + c + d;

                if (sum != 0 && sum != 4) {
                    flips += min(sum, 4 - sum);
                }
            }
        }

        return flips;
    }
};
