class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        if (n == 1 || m == 1) {
            int ans = 0;
            if (n == 1) {
                for (int j = 0; j < m / 2; ++ j) {
                    if (grid[0][j] != grid[0][m - j - 1])  {
                        ans ++;;
                    }
                }
            } else {
                for (int i = 0; i < n / 2; ++ i) {
                    if (grid[i][0] != grid[n - i - 1][0]) {
                        ans ++;
                    }
                }
            }
            int one = count1(grid);
            int more1 = one - ans;

            more1 %= 4;
            if (more1 >= 2 && ans == 0) {
                ans += 2;
            }

            if (more1 % 2 == 1) {
                ans ++;
                more1 --;
            }
            return ans;
        }

        int ans = 0;
        for (int i = 0; i < n / 2; ++ i) {
            for (int j = 0; j < m / 2; ++ j) {
                int cnt = 0;
                int x1 = grid[i][j];
                int x2 = grid[i][m - j - 1];
                int x3 = grid[n - i - 1][j];
                int x4 = grid[n - i - 1][m - j - 1];
                if (x1 == 1) cnt ++;
                if (x2 == 1) cnt ++;
                if (x3 == 1) cnt ++;
                if (x4 == 1) cnt ++;
                ans += min(cnt, 4 - cnt);
            }
        }

        int cc = 0;
        int oo = 0;
        if ((n % 2 == 1)) {
            for (int j = 0; j < m / 2; ++ j) {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1]) {
                    cc ++;
                } else if (grid[n / 2][j] == 1) {
                    oo += 2;
                }
            }
        }
        if (m % 2 == 1) {
            for (int i = 0; i < n / 2; ++ i) {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2]) {
                    cc ++; 
                } else if (grid[i][m / 2] == 1) {
                    oo += 2;
                }
            }
        }
        oo %= 4;

        if (oo == 0)    ans += cc;
        else if (cc > 0)    ans += cc;
        else ans += cc + 2;
        if ((n % 2 == 1) && (m % 2 == 1)) {
            if (grid[n / 2][m / 2] == 1) {
                ans ++;
            }
        }

        return ans;
    }


    int count1(vector<vector<int>>& grid) {
        int cnt = 0;
        for (int i = 0; i < grid.size(); ++ i) {
            for (int j = 0; j < grid[i].size(); ++ j) {
                if (grid[i][j] == 1) {
                    cnt ++;
                }
            }
        }

        return cnt;
    }
};
