class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int ans = 0;
        for(int i = 0; i < m / 2; i++){
            for(int j = 0; j < n / 2; j++){
                int t = grid[i][j] + grid[m - 1 - i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][n - 1 - j];
                if(t == 1 || t == 3) ans += 1;
                if(t == 2) ans += 2;
            }
        }
        int t1 = 0, t2 = 0;
        if(m % 2 == 1){
            int a = 0, b = n - 1;
            while(a < b){
                if(grid[m / 2][a] != grid[m / 2][b]){
                    ans++;
                    t1 = 1;
                }
                a++;
                b--;
            }
        }
        if(n % 2 == 1){
            int a = 0, b = m - 1;
            while(a < b){
                if(grid[a][n / 2] != grid[b][n / 2]){
                    ans++;
                    t2 = 1;
                } 
                a++;
                b--;
            }
        }
        int k = 0;
        if(m % 2 == 1 && t1 == 0 && n % 2 == 1 && t2 == 0){
            for(int j = 0; j < n / 2; j++){
                if(grid[m/2][j]) k++;
            }
            for(int i = 0; i < m / 2; i++){
                if(grid[i][n/2]) k++;
            }
        }
        else if(m % 2 == 1 && t1 == 0 && n % 2 == 0){
            for(int j = 0; j < n / 2; j++){
                if(grid[m/2][j]) k++;
            }
        }
        else if(n % 2 == 1 && t2 == 0 && m % 2 == 0){
            for(int i = 0; i < m / 2; i++){
                if(grid[i][n/2]) k++;
            }
        }
        if(k % 2 == 1) ans += 2;
        if(m % 2 == 1 && n % 2 == 1){
            ans += grid[m / 2][n / 2];
        }
        return ans;
    }
};
