class Solution {
public:


    int minFlips(vector<vector<int>>& grid) {

        int row = grid.size();
        int col = grid[0].size();

        int count = 0;
        int loc;

        for(int i=0; i<row/2; i++)
        {
            for(int j=0; j<col/2; j++)
            {
                loc = grid[i][j];
                loc += grid[row-i-1][j];
                loc += grid[i][col-j-1];
                loc += grid[row-i-1][col-j-1];
                count += min(loc, 4-loc);
            }
        }

        //std::cout << "count " << count << std::endl;

        int one_count = 0;
        int change = 0;
        if(row%2!=0)
        {
            for(int j=0; j<col/2; j++)
            {
                if(grid[row/2][j]!=grid[row/2][col-j-1])
                {
                    grid[row/2][j]=0;
                    grid[row/2][col-j-1]=0;
                    change++;
                }
            }

            for(int j=0; j<col/2; j++)
                if(grid[row/2][j]==1)
                    one_count+=2;
        }

        //std::cout << "change " << change << std::endl;

        if(col%2!=0)
        {
            for(int i=0; i<row/2; i++)
            {
                if(grid[i][col/2]!=grid[row-i-1][col/2])
                {
                    grid[i][col/2]=0;
                    grid[row-i-1][col/2]=0;
                    change++;
                }
            }

            for(int j=0; j<row/2; j++)
                if(grid[j][col/2]==1)
                    one_count+=2;
        }

        //std::cout << "total change " << change << std::endl;

        if(col%2!=0 && row%2!=0)
            if(grid[row/2][col/2]==1)
                count++;

        if(one_count%4==0)
            return count+change;

        if(change!=0)
            return count+change;

        return count+2;
    }
};

