class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = 0;
        // for(int x = 1; x <= n/2; x++) {
        //     for(int y = 1; y <= m/2; y++) {
        //         int a = grid[cx - x][cy-y], b = grid[cx - x][cy + y];
        //         int c = grid[cx + x][cy-y], d = grid[cx + x][cy + y];

        //     }
        // }
        int p1s_withchange = 0, p1s_nochange = 0;
        for(int i=0; i< n/2 + (n%2); i++) {
            for(int j=0; j< m/2 + (m%2); j++) {
                set<pair<int,int>> st = {{i,j},{n-i-1,j},{i,m-j-1},{n-i-1,m-j-1}};
                // for(auto p:st) cout<<p.first<<" "<<p.second<<endl;
                // cout<<"-----------"<<endl;
                if(st.size()==1) continue; 
                int a = grid[i][j], b = grid[n-i-1][j], c = grid[i][m-j-1], d = grid[n-i-1][m-j-1];
                int k = a+b+c+d;
                if(st.size()==2) {
                    k /= 2;
                    if(k==2) p1s_nochange++;
                    else if(k==1) {
                        p1s_withchange++;
                        ans++;
                    }
                    continue;
                }
                if(k==4 || k==0) continue;
                else if(k<=2) ans += k;
                else ans += 4-k;
            }
        }
        // cout<<p1s_withchange<<" "<<p1s_nochange<<endl;
        if((p1s_withchange + p1s_nochange) % 2 != 0) {
            if(p1s_withchange == 0) ans += 2;
        } 
        if((n*m) % 2 != 0) ans += grid[n/2][m/2];
        return ans;
    }
};
