class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size(), a, b, s[3] = {0}, r = 0;

        if (n % 2 && m % 2 && g[n / 2][m / 2]) r += 1;

        if (n % 2) {
            for (a = 0, b = m - 1; a < b; ++a, --b) {
                s[g[n / 2][a] + g[n / 2][b]]++;
            }
        }

        if (m % 2) {
            for (a = 0, b = n - 1; a < b; ++a, --b) {
                s[g[a][m / 2] + g[b][m / 2]]++;
            }
        }

        if (s[2] % 2) r += s[1] > 0 ? s[1] : 2;
        else r += s[1];

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int x = g[i][j], y = g[n - 1 - i][j], z = g[i][m - 1 - j], w = g[n - 1 - i][m - 1 - j];
                int sum = x + y + z + w;
                if (sum && sum != 4) r += min(sum, 4 - sum);
            }
        }

        return r;
    }
};
