func minFlips(grid [][]int) int {

	n := len(grid)
	m := len(grid[0])

    // var midRow = -1
    var diffCnt int
    // var up, down, left, right int
    var oneCnt int
	if n%2 != 0 {
		mid := n / 2
		for j := 0; j < m/2; j++ {
            if grid[mid][j] != grid[mid][m-1-j] {
                diffCnt++
            } else if grid[mid][j] == 1 {
                oneCnt+=2
            }
		}
        //midRow = mid
	}
    if m%2 != 0 {
        mid := m/2
        for i := 0; i < n/2; i++ {
            if grid[i][mid] != grid[n-1-i][mid] {
                diffCnt++
            } else if grid[i][mid] == 1 {
                oneCnt+=2
            }
        }
    }

    var cnt int
    cnt += diffCnt
    if oneCnt%4 != 0 && diffCnt == 0 {
        cnt+=2
    }    
    if m%2 != 0 && n%2 != 0 && grid[n/2][m/2] == 1 {
        cnt++
    }

    halvN := n/2
    halvM := m/2
    for i := 0; i < n; i++ {
        for j := 0; j <m; j++ {
            if j <halvM && i < halvN {
                sum := grid[i][j] + grid[i][m-1-j] + grid[n-1-i][j] + grid[n-1-i][m-1-j]
                if sum == 1 || sum == 3 {
                    cnt++
                }
                if sum == 2 {
                    cnt+=2
                }
            }
        }
    }

    return cnt

}
