class Solution
{
public:
    int minFlips(vector<vector<int>> &grid)
    {
        int n = grid.size();
        int m = grid[0].size();

        int x1 = 0;
        int x2 = n - 1;

        int count = 0;

        int extraOnes = 0;
        int supportingOnes = 0;

        while (x1 <= x2)
        {

            int y1 = 0;
            int y2 = m - 1;
            while (y1 <= y2)
            {
                int one = 0;
                int zero = 0;
                if (x1 == x2 && y1 == y2)
                {
                    if (grid[x1][y1] == 1)
                    {
                        count++;
                    }
                }
                else if (y1 == y2)
                {
                    if (grid[x1][y1] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    if (grid[x2][y1] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    if (one == 2)
                    {
                        extraOnes += 2;
                    }
                    else if (one == 1)
                    {
                        count++;
                        supportingOnes += 2;
                    }
                }
                else if (x1 == x2)
                {
                    if (grid[x1][y1] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    if (grid[x1][y2] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    if (one == 2)
                    {
                        extraOnes += 2;
                    }
                    else if (one == 1)
                    {
                        count++;
                        supportingOnes += 2;
                    }
                }
                else
                {
                    if (grid[x1][y1] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    if (grid[x1][y2] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    if (grid[x2][y1] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    if (grid[x2][y2] == 1)
                    {
                        one++;
                    }
                    else
                    {
                        zero++;
                    }
                    count += min(one, zero);
                }

                y1++;
                y2--;
            }
            x1++;
            x2--;
        }
        int rem = extraOnes % 4;
        if (rem == 2 && supportingOnes == 0)
            count += 2;

        return count;
    }
};
