function minFlips(grid: number[][]): number {
  const m = grid.length,
    n = grid[0].length

  let res = 0,
    a = 0,
    one = 0
  for (let i = 0; i < m / 2; i++) {
    for (let j = 0; j < n / 2; j++) {
      if (i < Math.floor(m / 2) && j < Math.floor(n / 2)) {
        let x = grid[i][j] + grid[i][n - j - 1] + grid[m - i - 1][j] + grid[m - i - 1][n - j - 1]
        if (x === 1 || x === 3) res++
        else if (x === 2) res += 2
      } else if (m & 1 && n & 1 && i === Math.floor(m / 2) && j === Math.floor(n / 2)) {
        res += grid[i][j]
      } else if (m & 1 && i === Math.floor(m / 2)) {
        if (grid[i][j] !== grid[i][n - j - 1]) a++
        else one += grid[i][j] * 2
      } else if (n & 1 && j === Math.floor(n / 2)) {
        if (grid[i][j] !== grid[m - i - 1][j]) a++
        else one += grid[i][j] * 2
      }
    }
  }

  if (one % 4 === 0 || a) res += a
  else res += 2
  return res
}
