class Solution {

    public int countMirrors(int x, int y, int n, int m){
        int res = 1;
        boolean both = true;

        if(y < m-y-1){
            res++;
        } else {
            both = false;
        }

        if(x < n-x-1){
            res++;
        } else {
            both = false;
        }

        if(both){
            res++;
        }

        return res;
    }

    public int getOnes(int x, int y, int[][] grid){
        int n = grid.length;
        int m = grid[0].length;

        int res = grid[x][y];
        boolean both = true;

        if(y < m-y-1){
            res += grid[x][m-y-1];
        } else {
            both = false;
        }

        if(x < n-x-1){
            res += grid[n-x-1][y];
        } else {
            both = false;
        }

        if(both){
            res += grid[n-x-1][m-y-1];
        }

        return res;
    }

    public int minFlips(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;

        int twos = 0;
        int ops = 0;
        int twoButOnes = 0;

        for(int i = 0 ; i < (n+1)/2 ; i++){
            for(int j = 0 ; j < (m+1)/2 ; j++){
                int count = countMirrors(i, j, n, m);
                int ones = getOnes(i, j, grid);

                if(count == 4){
                    ops += Math.min(ones, 4-ones);
                    // System.out.println(4 + " " + i + " " + j + " " + ones);
                }
                else if(count == 2) {
                    if(ones == 2){
                        twos++;
                    } else if(ones == 1){
                        twoButOnes++;
                    } 
                    // System.out.println(2 + " " + i + " " + j + " " + ones);
                } else {
                    ops += ones;
                    // System.out.println(1 + " " + i + " " + j + " " + ones);
                }
            }
        }

        boolean isOddTwo = twos % 2 == 1;

        if(!isOddTwo){
            return ops + twoButOnes;
        }

        if(twoButOnes > 0){
            ops += 1;
            twoButOnes--;
        } else {
            ops += 2;
        }

        return ops + twoButOnes;
    }
}
