class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int count = 0;
        int m = grid.size(), n = grid[0].size();

        for (int i = 0; i < m / 2; i++) {
            for (int j = 0; j < n / 2; j++) {
                // if (grid[i][j] == 0) {
                //     continue;
                // }

                int onescount = grid[i][j] + grid[m - i - 1][j] + grid[i][n - j - 1] + grid[m - i - 1][n - j - 1];
                if (onescount == 1) {
                    count += 1;
                } else if (onescount == 2) {
                    count += 2;
                } else if (onescount == 3) {
                    count += 1;
                }
                grid[i][j] = grid[m - i - 1][j] = grid[i][n - j - 1] = grid[m - i - 1][n - j - 1] = 0;
            }
        }

        int pairs = 0;
        int tmpcount = 0;
        if (m % 2) {
            int row = m / 2;
            int left = 0, right = n - 1;

            while (left < right) {
                if (grid[row][left] != grid[row][right]) {
                    tmpcount++;
                } else if (grid[row][left] == 1) {
                    pairs++;
                }
                left++;
                right--;
            }
        }

        if (n % 2) {
            int col = n / 2;
            int top = 0, bottom = m - 1;

            while (top < bottom) {
                if (grid[top][col] != grid[bottom][col]) {
                    tmpcount++;
                } else if (grid[top][col] == 1) {
                    pairs++;
                }
                top++;
                bottom--;
            }
        }
        if (pairs % 2) {
            tmpcount += tmpcount > 0 ? 0 : 2;
        }
        count += tmpcount;

        if (m % 2 && n % 2) {
            count += grid[m / 2][n / 2];
        }

        return count;
    }
};

