class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int c=0,p=0,o=0;
       for(int i=0;i<grid.size()/2+grid.size()%2;i++)
       {
         for(int j=0;j<grid[0].size()/2+grid[0].size()%2;j++)
         {
            if(i==grid.size()-1-i&&j==grid[0].size()-1-j)
            {
               if(grid[i][j]==1)
               {
                c++;
               }
            }
            else if(i==grid.size()-1-i)
            {
                if(grid[i][j]!=grid[i][grid[0].size()-j-1])
                {
                    c++;
                    p=1;
                }
                else if(grid[i][j]==1)
                {
                    o+=2;
                }
            }
             else if(j==grid[0].size()-1-j)
            {
                if(grid[i][j]!=grid[grid.size()-i-1][j])
                {
                    c++;
                    p=1;
                }
                else if(grid[i][j]==1)
                {
                    o+=2;
                }
            }
            else
            {
                int cnt1=0;
                int cnt2=0;
                if(grid[i][j]==1)
                {
                    cnt1++;
                }
                else if(grid[i][j]==0)
                {
                    cnt2++;
                }
                 if(grid[grid.size()-i-1][j]==1)
                {
                    cnt1++;
                }
                else if(grid[grid.size()-i-1][j]==0)
                {
                    cnt2++;
                }
                 if(grid[grid.size()-i-1][grid[0].size()-j-1]==1)
                {
                    cnt1++;
                }
                else if(grid[grid.size()-i-1][grid[0].size()-j-1]==0)
                {
                    cnt2++;
                }
                 if(grid[i][grid[0].size()-j-1]==1)
                {
                    cnt1++;
                }
                else if(grid[i][grid[0].size()-j-1]==0)
                {
                    cnt2++;
                }
                if(cnt1==3||cnt2==3)
                {
                    c++;
                }
                else if(cnt1==2||cnt2==2)
                {
                    c+=2;
                }
            }
         }
       }
       if(o%4!=0&&p!=1)
       {
        c+=2;
       }
       return c;
    }
};
