class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        int ans = 0;
        for(int i = 0 ; i < n/2 ; i++)
        {
            for(int j = 0 ; j < m/2 ; j++)
            {
                int c0 = 0, c1 = 0;
                if(grid[i][j] == 1) c1++;
                else c0++;
                if(grid[i][m-j-1] == 1) c1++;
                else c0++;
                if(grid[n-i-1][j] == 1) c1++;
                else c0++;
                if(grid[n-i-1][m-j-1] == 1) c1++;
                else c0++;

                if(c0 == 4 or c0 == 0) continue;
                else if(c0 == 1 or c0 == 3) ans++;
                else ans += 2;
            }
        }

        if(n%2 == 1 and m%2 == 1)
        {
            if(grid[n/2][m/2] == 1) ans++;

            int countofpairsof1 = 0, countofdiff = 0;

            for(int i = 0 ; i < n/2 ; i++)
            {
                if(grid[i][m/2] != grid[n-i-1][m/2]) countofdiff++;
                else if(grid[i][m/2] == 1) countofpairsof1++;
            }

            for(int j = 0 ; j < m/2 ; j++)
            {
                if(grid[n/2][j] != grid[n/2][m-j-1]) countofdiff++;
                else if(grid[n/2][j] == 1) countofpairsof1++;
            }

            if(countofpairsof1 > 0)
            {
                if(countofpairsof1 % 2 == 0) ans += countofdiff;
                else 
                {
                    if(countofdiff == 0) ans += 2;
                    else ans += countofdiff;
                }
            }
            else
            {
                ans += countofdiff;
            }
        }
        else if(n%2 == 1)
        {
            int countofpairsof1 = 0, countofdiff = 0;
            for(int j = 0 ; j < m/2 ; j++)
            {
                if(grid[n/2][j] != grid[n/2][m-j-1]) countofdiff++;
                else if(grid[n/2][j] == 1) countofpairsof1++;
            }

            if(countofpairsof1 > 0)
            {
                if(countofpairsof1 % 2 == 0) ans += countofdiff;
                else 
                {
                    if(countofdiff == 0) ans += 2;
                    else ans += countofdiff;
                }
            }
            else
            {
                ans += countofdiff;
            }
        }
        else if(m%2 == 1)
        {
            int countofpairsof1 = 0, countofdiff = 0;

            for(int i = 0 ; i < n/2 ; i++)
            {
                if(grid[i][m/2] != grid[n-i-1][m/2]) countofdiff++;
                else if(grid[i][m/2] == 1) countofpairsof1++;
            }

            if(countofpairsof1 > 0)
            {
                if(countofpairsof1 % 2 == 0) ans += countofdiff;
                else 
                {
                    if(countofdiff == 0) ans += 2;
                    else ans += countofdiff;
                }
            }
            else
            {
                ans += countofdiff;
            }
        }
        return ans;
    }
};
