class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {
        int res = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                int tmp = grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                tmp = std::min(tmp,4-tmp);
                res+=tmp;
            }
        }

        int o = 0;
        int x = 0;
        if(n%2==1) {
            for(int j=0;j<m/2;j++) {
                if(grid[n/2][j]!=grid[n/2][m-j-1]) {
                    x++;
                } else if(grid[n/2][j]==1) {
                    o+=2;
                }
            }
        }

        if(m%2==1) {
            for(int i=0;i<n/2;i++) {
                if(grid[i][m/2]!=grid[n-i-1][m/2]) {
                    x++;
                } else if(grid[i][m/2]==1) {
                    o+=2;
                }
            }
        }

        res+=x;
        if(o%4==2 && x==0) {
            res+=2;
        }

        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1) {
            res++;
        }
        return res;
    }
};
