#define ll long long
class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        ios_base::sync_with_stdio(false);
    cin.tie(NULL);
        ll n = g.size(), m = g[0].size(), a = 0, p = 0, c = 0;

        ll i = 0;
        while (i < n / 2) {
            ll j = 0;
            while (j < m / 2) {
                ll o = g[i][j] + g[n - i - 1][j] + g[i][m - j - 1] + g[n - i - 1][m - j - 1];
                a += min(o, 4 - o);
                j++;
            }
            i++;
        }

        if (n % 2) {
            ll j = 0;
            while (j < m / 2) {
                g[n / 2][j] != g[n / 2][m - j - 1] ? (a++, p++) : (g[n / 2][j] ? c++ : 0);
                j++;
            }
        }

        if (m % 2) {
            ll i = 0;
            while (i < n / 2) {
                g[i][m / 2] != g[n - i - 1][m / 2] ? (a++, p++) : (g[i][m / 2] ? c++ : 0);
                i++;
            }
        }

        n % 2 && m % 2 && g[n / 2][m / 2] ? a++ : 0;
        !p && c % 2 ? a += 2 : 0;

        return a;
    }
};
