class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int midRowIndex=-1;
        int midColumnIndex=-1;
        int buffer=0;
        int pairCount=0;
        if(m%2!=0){
            midRowIndex=m/2;
        }
        if(n%2!=0){
            midColumnIndex=n/2;
        }
        int flips=0;
        for(int i=0;i<grid.size();i++){
            for(int j=0;j<grid[0].size();j++){
                if(i==midRowIndex&&j==midColumnIndex){
                    if(grid[i][j]==1){
                        grid[i][j]=0;
                        flips++;
                    }
                    else{
                        continue;
                    }
                }
                if(i==midRowIndex){
                    if(grid[i][j]!=grid[i][n-1-j]){
                        flips++;
                        buffer++;
                        grid[i][j]=0;
                        grid[i][n-1-j]=0;
                    }
                    else if(grid[i][j]==1){
                        pairCount++;
                        grid[i][j]=0;
                        grid[i][n-1-j]=0;
                    }
                    continue;
                }
                if(j==midColumnIndex){
                    if(grid[i][j]!=grid[m-1-i][j]){
                        flips++;
                        buffer++;
                        grid[i][j]=0;
                        grid[m-1-i][j]=0;
                    }
                    else if(grid[i][j]==1){
                        pairCount++;
                        grid[i][j]=0;
                        grid[m-1-i][j]=0;
                    }
                    continue;
                }
                int one = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j];
                int zero = 4-one;
                flips = flips+min(one,zero);
                grid[i][j]=0;
                grid[i][n-1-j]=0;
                grid[m-1-i][j]=0;
                grid[m-1-i][n-1-j]=0;
            }
        }
        if(pairCount%2){
            if(buffer==0){
                flips+=2;
            }
        }
        return flips;
    }
};
