class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        a = 0
        for i in range(n // 2):
            for j in range(m // 2):
                t = grid[i][j] + grid[i][-j-1]+grid[-i-1][j]+grid[-i-1][-j-1]
                a += min(t, 4-t)
        if n % 2 and m % 2:
            if grid[n//2][m//2]:
                a += 1
            ex = 0
            ones = 0
            for i in range(n//2):
                ones += grid[i][m//2] + grid[-i-1][m//2]
                if grid[i][m//2] != grid[-i-1][m//2]:
                    ex += 1
            for j in range(m//2):
                ones += grid[n//2][j] + grid[n//2][-j-1]
                if grid[n//2][j] != grid[n//2][-j-1]:
                    ex += 1
            if ex == 0:
                if ones % 4:
                    return a + 2
            return a+ex
        if n % 2:
            ex = 0
            ones = 0
            for j in range(m // 2):
                ones += grid[n // 2][j] + grid[n // 2][-j - 1]
                if grid[n // 2][j] != grid[n // 2][-j - 1]:
                    ex += 1
            if ex == 0:
                if ones % 4:
                    return a + 2
            return a+ex
        if m % 2:
            ex = 0
            ones = 0
            for i in range(n // 2):
                ones += grid[i][m // 2] + grid[-i - 1][m // 2]
                if grid[i][m // 2] != grid[-i - 1][m // 2]:
                    ex += 1
            if ex == 0:
                if ones % 4:
                    return a + 2
            return a+ex
        return a
