class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size();
        int ans = 0;
        int sum2 = 0, sum1 = 0;
        for (int i = 0; i < (m + 1) / 2; ++i) {
            int l = 0, r = n - 1;
            while (l < r) {
                if (m - 1 - i != i) {
                    int a = grid[i][l], b = grid[i][r];
                    int c = grid[m - 1 - i][l], d = grid[m - 1 - i][r];
                    int cnt = a + b + c + d; 
                    //cout<<i<<" "<<cnt<<" "<<a<<" "<<b<<" "<<c<<" "<<d<<endl;
                    int cur = min(cnt, 4 - cnt);
                    ans += cur;
                } else {
                    int a = grid[i][l], b = grid[i][r];
                    int cnt = a + b; 
                  //  cout<<i<<" "<<cnt<<endl;
                    int cur = min(cnt, 2 - cnt);
                    ans += cur;
                    if (cnt == 2) sum2 += 2;
                    if (cnt == 1) sum1 += 1;
                }
                ++l;
                --r;
            }
            if (l == r) {
                if (m - 1 - i != i) {
                    int a = grid[i][l];
                    int c = grid[m - 1 - i][l];
                    if (a != c) ++ans;

                    if (a + c == 2) sum2 += 2;
                    if (a + c == 1) sum1 += 1;
                } else {
                    if (grid[i][l] == 1) ++ans;
                }
            }
        }
        if ((sum2 % 4) != 0) {
             if (sum1 > 0) ans += 0;
             else ans+= 2;
        }
        return ans;
    }
};
